import React from "react";
import axios from "axios";
import Movie from "./Movie";
import "./index.css";
import Menu from "./Menu.js";

class App extends React.Component {
  state = {
    is_loading: true,
    movies: [],
  };

  getMovies = async () => {
    const {
      data: {
        data: {movies},
      },
    } = await axios.get("https://yts.mx/api/v2/list_movies.json?sort_by=raiting")

    this.setState({movies, is_loading: false})
  }

  componentDidMount() {
    this.getMovies()
  }

  render() {
    const {is_loading, movies} = this.state

    return (<section className="container ">
    {
      is_loading
      ? <div className="loader">
          <span className="loader_text">Loading...</span>
        </div> 
      : movies.map((movie, id) => {
                    return (<div key={id}>
                              <Movie
                                key={movie.id}
                                id={movie.id} 
                                year={movie.year} 
                                title={movie.title} 
                                summary={movie.summary} 
                                poster={movie.medium_cover_image}
                                genres={movie.genres}
                                duration={movie.runtime}
                                language={movie.language.toUpperCase()}
                                rating={movie.rating}/>
                            </div>
                           )     
    })}</section>
  )}
}

export default App;
