import React from "react";
import PropTypes from "prop-types";
import "./index.css";

let words_counter = (description) => {
    let matches = description.split(/\s+/) || [] //word count

    if (matches.length > 33) {
      return description.slice(0, 180) + "..."
    }
    else {
        return description
    }
}

function Movie({id, year, title, summary, poster, genres, duration, language, rating}) {
    return <div className="movie py-5 px-10">
            <img src={poster} alt={title} title={title}/>
            <div className="movie_column text-left">
                <h1 className="movie_title">{title}</h1>
                <h5 className="description">{year} | {genres[0]} | {`${duration} min `} | {language}</h5>
                <h5 className="raiting">{rating} out of 10</h5>
                <p className="movie_summary">{words_counter(summary)}</p>
            </div>
           </div>
}

Movie.propTypes = {
    id: PropTypes.number.isRequired,
    year: PropTypes.number.isRequired,
    title: PropTypes.string.isRequired,
    summary: PropTypes.string.isRequired,
    poster: PropTypes.string.isRequired,
    genres: PropTypes.arrayOf(PropTypes.string).isRequired,
    duration: PropTypes.number.isRequired,
    language: PropTypes.string.isRequired,
    rating: PropTypes.number.isRequired,
}

export default Movie
