import PropTypes from "prop-types"
import React from "react";
import axios from "axios";
import Movie from "./Movie";

class App extends React.Component {
  state = {
    is_loading: true,
    movies: [],
  };

  getMovies = async () => {
    const {
      data: {
        data: {movies},
      },
    } = await axios.get("https://yts.mx/api/v2/list_movies.json?sort_by=raiting")

    this.setState({movies, is_loading: false})
  }

  componentDidMount() {
    this.getMovies()
  }

  render() {
    const {is_loading, movies} = this.state

    return <div>{is_loading ? "Loading..." : movies.map(movie => {
      return <Movie 
              id={movie.id} 
              year={movie.year} 
              title={movie.title} 
              summary={movie.summary} 
              poster={movie.medium_cover_image} 
            />
    })}</div>
  }
}

export default App;
